import React, { useState, useEffect } from 'react';
import '../styles/login.css';
import md5 from 'md5';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'universal-cookie';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login(props) {

    const baseUrl = "https://localhost:44370/api/usuarios";
    const cookies = new Cookies();
    const [form, setForm] = useState({
        nombreUsuario: '',
        clave: ''
    });

    const navigate = useNavigate();




    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({
            ...form,
            [name]: value
        })
        console.log(form);
    }

    const iniciarSesion = async () => {
        if (!form.nombreUsuario || !form.clave) { alert("Usuario y contraseña requeridos"); return };
        await axios.get(baseUrl + `/${form.nombreUsuario}/${form.clave}`)
            .then(response => {
                return response.data;
            }).then(response => {
                if (response) {
                    cookies.set('idUsuario', response.idUsuario, { path: '/' });
                    cookies.set('nombreUsuario', response.nombreUsuario, { path: '/' });
                    cookies.set('clave', response.clave, { path: '/' });
                    cookies.set('nombre', response.nombre, { path: '/' });
                    cookies.set('rol', response.rol, { path: '/' });
                    cookies.set('permiso', response.permiso, { path: '/' });
                    alert("Bienvenido " + response.nombre);
                    //this.props.history.push('usuarios');
                    navigate('/menu');
                } else {
                    alert('El usuario o contraseña no son correctos');
                }
            }).catch(error => {
                console.log(error);
            })
    }


    useEffect(() => {
        if (cookies.get('idUsuario')) {
            navigate('/menu');
        }
    });

    return (
        <div className='container'>
            <div className='form-group'>
                <h1>Bienvenido</h1>
                <div className=''>
                    <label>Usuario</label>
                    <input type="text" name="nombreUsuario" className='input-login' onChange={handleChange} /> 
                    <label>Contraseña</label>
                    <input type="text" name="clave" className='input-login' onChange={handleChange} />
                </div>
                <button className='boton-iniciar' onClick={() => iniciarSesion()}>Acceder</button>
            </div>

            <form>
  <div class="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password"/>
  </div>
  <div class="form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
          
        </div>
    )
}

export default Login;
