import React, { Component, useState, useEffect } from "react";
import ReactDOM from 'react-dom';
import axios from 'axios';
import Cookies from 'universal-cookie';
import { useNavigate, Link, } from 'react-router-dom';


function MenuPermitido(props) {
    let button;
    const cookies = new Cookies();
    const [dataPermisos, setDataPermisos] = useState([]);
    const getPermisosAsignados = async () => {
        const baseUrl = "https://localhost:44370/api/";
        const cookies = new Cookies();
        let rol = cookies.get('rol');
        if (cookies.get('idUsuario')) {
            await axios.get(baseUrl + `permisosasignados/${rol}`)
                .then(response => {
                    setDataPermisos(response.data);
                });
            console.log(dataPermisos.map(a => (console.log(a.rol))));
        }
    }
    dataPermisos.map(per => {
        debugger
        console.log(per);
        button =<li key={per.idRol}> <Link to="/permisos" >Permisos</Link></li>;
    })
    useEffect(() => {
        getPermisosAsignados();
    }, []);
    return (
        {button}
    );
}


class Renderizado extends Component {
    
    render() {
        
        let button;

        button = <Link to="/permisos" >Permisos</Link>;

        return (
            <>
            { button }
            <MenuPermitido/>
            </>
        )
    }
}


export default Renderizado;
