import React, { useState, useEffect } from 'react';
import Cookies from 'universal-cookie';
import { useNavigate, Route, Link, BrowserRouter, Switch } from 'react-router-dom';
import Usuarios from './Login';
import Login from './Login';
import '../styles/menu.css';
import logo from '../assets/logo-aranda.png';


function Menu() {
  const cookies = new Cookies();

  const navigate = useNavigate();


  const cerrarSesion = () => {
    cookies.remove('idUsuario', { path: '/' });
    cookies.remove('nombreUsuario', { path: '/' });
    cookies.remove('clave', { path: '/' });
    cookies.remove('nombre', { path: '/' });
    cookies.remove('rol', { path: '/' });
    cookies.remove('permiso', { path: '/' });
    navigate('../');
  }

  useEffect(() => {
    if (!cookies.get('idUsuario')) {
      navigate('../');
    }
  });

  return (
    <>

      <button className='btn btn-danger' onClick={() => cerrarSesion()}>Cerrar Sesi√≥n</button>
      <h1>Id:{cookies.get('idUsuario')}</h1>
      <h1>{cookies.get('nombreUsuario')}</h1>




      <div>
        <h1>ArandaSoft</h1>
        <nav
          style={{
            borderBottom: "solid 1px",
            paddingBottom: "1rem"
          }}>
          <Link to="/usuarios">Invoices</Link> |{" "}
          <Link to="/expenses">Expenses</Link>
        </nav>
      </div>
      <header className="page-header">
        <nav>

          <img src={logo} alt='logo' className='logo ' />
          <button className="toggle-mob-menu" aria-expanded="false" aria-label="open menu">

          </button>
          <ul className="admin-menu mt-2">
            <li className="menu-heading">
              <h3>{cookies.get('nombreUsuario')}</h3>
            </li>
            <li>
              <a href="#0">
                <span>Permisos</span>
              </a>
            </li>
            <li>
              <div className="switch">
                <label>
                  <span>Roles</span>
                </label>
              </div>
              <Link to="/usuarios">Usuarios</Link>
            </li>
          </ul>
        </nav>
      </header>
    </>
  )
}


export default Menu;
