import React, { useState, useEffect } from 'react';
import Cookies from 'universal-cookie';
import { useNavigate, Route,Link ,BrowserRouter,Switch} from 'react-router-dom';
import Usuarios from './Login';
import Login from './Login';


function Menu() {
  const cookies = new Cookies();

  const navigate = useNavigate();


  const cerrarSesion = () => {
    cookies.remove('idUsuario', { path: '/' });
    cookies.remove('nombreUsuario', { path: '/' });
    cookies.remove('clave', { path: '/' });
    cookies.remove('nombre', { path: '/' });
    cookies.remove('rol', { path: '/' });
    cookies.remove('permiso', { path: '/' });
    navigate('../');
  }

  useEffect(() => {
    if (!cookies.get('idUsuario')) {
      navigate('../');
    }
  });

  return (
    <>
      <button className='btn btn-danger' onClick={() => cerrarSesion()}>Cerrar Sesi√≥n</button>
      <h1>Id:{cookies.get('idUsuario')}</h1>
      <h1>{cookies.get('nombreUsuario')}</h1>

      
      <div>
      <h1>Bookkeeper</h1>
      <nav
        style={{
          borderBottom: "solid 1px",
          paddingBottom: "1rem"
        }}
      >
        <Link to="/usuarios">Invoices</Link> |{" "}
        <Link to="/expenses">Expenses</Link>
      </nav>
    </div>
    </>
  )
}


export default Menu;
