import React, { useEffect } from 'react';
import Cookies from 'universal-cookie';
import { useNavigate, Link, } from 'react-router-dom';
import '../styles/menu.css';
import logo from '../assets/logo-aranda.png';
import logoSinco from '../assets/logoSinco.png';
import axios from 'axios';



function Menu() {
  const cookies = new Cookies();

  const navigate = useNavigate();


  const cerrarSesion = () => {
    cookies.remove('idUsuario', { path: '/' });
    cookies.remove('nombreUsuario', { path: '/' });
    cookies.remove('clave', { path: '/' });
    cookies.remove('nombre', { path: '/' });
    cookies.remove('apellidos', { path: '/' });
    cookies.remove('rol', { path: '/' });
    cookies.remove('permiso', { path: '/' });
    cookies.remove('edad', { path: '/' });
    cookies.remove('email', { path: '/' });
    cookies.remove('telefono', { path: '/' });
    cookies.remove('direccion', { path: '/' });
    cookies.remove('clave', { path: '/' });
    sessionStorage.removeItem('idRol');
    sessionStorage.removeItem('rol');
    sessionStorage.removeItem('permisos');
    navigate('../');
  }

  let rol = cookies.get("rol");

  let display = '';
  let displayUser = '';
  display = rol === "1" ? 'none' : 'block';
  displayUser = rol === "1" ? 'none' : 'block';
  const validaPer = () => {
    let permisos = sessionStorage.getItem('permisos');
  }

  const getPermisosAsignados = async () => {
    const baseUrl = "https://localhost:44370/api/";
    const cookies = new Cookies();
    let rol = cookies.get('rol');
    if (cookies.get('idUsuario')) {
      await axios.get(baseUrl + `permisosasignados/${rol}`)
        .then(response => {
          return response.data;
        }).then(response => {
          if (response) {
            const permisos = [];
            const idRol = response[0].idRol;
            const Rol = response[0].rol;
            response.forEach(element => {
              permisos.push(element.idPermiso);
            });
            console.log(permisos);
            sessionStorage.setItem('idRol', idRol);
            cookies.set('idRol', idRol, { path: '/' });
            sessionStorage.setItem('rol', Rol);
            sessionStorage.setItem('permisos', permisos);
          }
        });
    }
    let permisos = sessionStorage.getItem('permisos');
     let arr = (permisos.length>0 ? permisos.split(',') : []);
    for (let i = 0; i < arr.length; i++) {
      const element = arr[i];
      debugger
      if (element === "2") {
        displayUser = 'block';
        display = 'none';
      }
      console.log(element);
    }
  }


  useEffect(() => {
    if (!cookies.get('idUsuario')) {
      navigate('../');
    }
    validaPer();
    getPermisosAsignados();
  });

  //style={{display: cookies.get("rol")==='1' ? 'none' : 'block' }}
  return (
    <>
      <div className='usuario'>
        <div className='info ml-2'>
          <span>Bienvenido <b>{cookies.get('nombreUsuario')}</b></span>
          <h4>{cookies.get('nombre')} {cookies.get('apellidos')}</h4>
        </div>

      </div>
      {/* <div>
        <p>
          {cookies.get('nombre')} {cookies.get('apellidos')}
        </p>
        <h1>Id:{cookies.get('idUsuario')}</h1>
        <h1>{cookies.get('nombreUsuario')}</h1>
      </div> */}
      <header className="page-header" >
        <nav>
          <Link to='../perfil'>
            <img srcSet={logoSinco} alt='logo' className='logo ' />
          </Link>
          <ul className="admin-menu mt-2">
            <li className="menu-heading">
              <h3>{cookies.get('nombreUsuario')}</h3>
              <label>{sessionStorage.getItem("rol")}</label>
              <hr />
            </li>
            <li>
              <Link to="/welcome">Home</Link>
            </li>
            <li>
              <Link to="/usuarios" style={{ display: displayUser }}>Usuarios</Link>
            </li>
            <li>
              <Link to="/roles" style={{ display: display }}>Roles</Link>
            </li>
            <li>
              <Link to="/permisos" style={{ display: display }}>Permisos</Link>
            </li>
            <li>
              <Link to="/permisosAsignados" style={{ display: display }}>Asignación de permisos</Link>
            </li>
            <li>
              <Link to="/perfil" style={{ display: display }}>Perfil</Link>
            </li>
          </ul>
          <ul className='mt-2'>
            <li>
              <button className='btn btn-danger' onClick={() => cerrarSesion()}>Cerrar Sesión</button>
            </li>
          </ul>
        </nav>
      </header>
    </>
  )
}


export default Menu;
